import { Component, Input } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-book',
  templateUrl: './book.component.html',
  styleUrls: ['./book.component.css']
})
export class BookComponent {
  @Input()
  bookLocal:any;

  constructor(private router: Router){

  }
showDetails() {
  console.log("saliendo: " + this.bookLocal);
  this.router.navigate(['booklist', this.bookLocal])
}

}
<div class="container" style="width: 80%;" (click)="showDetails()">
  <h2 class=" d-flex justify-content-center">Detalles {{bookLocal.titulolibro}}</h2>
  <div class=" d-flex justify-content-center">
    <label>Titulo: </label>
    <label> {{bookLocal.titulolibro}}</label>
  </div>
  <div class=" d-flex justify-content-center">
    <label>Autor: </label>
    <label> {{bookLocal.autorlibro}}</label>
  </div>
  <div class=" d-flex justify-content-center">
    <label>Categoria: </label>
    <label>{{bookLocal.idcategoriaNavigation.nombrecategoria}}</label>
  </div>
  <div class=" d-flex justify-content-center">
    <label>Resumen: </label>
    <label> {{bookLocal.resumenlibro}}</label>
  </div>
</div>
