import { Component } from '@angular/core';
import { RegisterServiceService } from '../Services/register/register.service.service';
import {Router} from '@angular/router'

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})

export class RegisterComponent {

  private aspNetUser = {
    id: "",
    userName: "",
    normalizedUserName: null,
    email: "",
    normalizedEmail: null,
    emailConfirmed: false,
    passwordHash: "",
    securityStamp: null,
    concurrencyStamp: null,
    phoneNumber: null,
    phoneNumberConfirmed: false,
    twoFactorEnabled: false,
  }

  private response: any;

  email:any = "";
  password:any = "";

  constructor(private _registerService: RegisterServiceService,
    private router: Router
  ){

  }

  register(){
    this.aspNetUser.userName = this.email;
    this.aspNetUser.email = this.email;
    this.aspNetUser.passwordHash = this.password;
    this._registerService.PostAspNetUser(this.aspNetUser).subscribe(
      data => {
        this.response = data;
        console.info(this.response);
        this.router.navigate(['']);
      },
      error=>{
        console.error(error);
      }
    )
  }
}
<div class="container" style="width: 30%;">
  <h2 class=" d-flex justify-content-center">Registro</h2>
  <div class="form-floating mb-3  d-flex justify-content-center">
      <input class="form-control" placeholder="name@example.com" type="text" [(ngModel)]='email'/>
      <label class="form-label">Email</label>
  </div>
  <div class="form-floating mb-3  d-flex justify-content-center">
      <input class="form-control" placeholder="password" type="password" [(ngModel)]='password'/>
      <label class="form-label">Password</label>
  </div>
  <div class="form-floating mb-3  d-flex justify-content-center">
    <input class="form-control" placeholder="password" type="password" />
    <label class="form-label">Confirmar Password</label>
  </div>
  <div>
      <button class="w-100 btn btn-lg btn-primary  d-flex justify-content-center"
      id="login-submit" (click)="register()">Registrar</button>
  </div>
</div>
