import { Component, OnInit } from '@angular/core';
import { BookServiceService } from 'src/app/Library/Services/book.service.service';


@Component({
  selector: 'app-list-books',
  templateUrl: './list-books.component.html',
  styleUrls: ['./list-books.component.css']
})
export class ListBooksComponent implements OnInit {

  booksList:any[] = [];

  constructor(private _bookService: BookServiceService){

  }

  ngOnInit(): void {
    (this._bookService.getAllBooks()).subscribe(
      data=>{
        this.booksList = data;
      },error=>{
        console.error(error);
      });
  }
}
<div class="container" style="width: 30%;">
  <h2 class=" d-flex justify-content-center">Libros</h2>
  <app-book *ngFor="let book of booksList" [bookLocal]="book">

  </app-book>
</div>
