import { Component, Input, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ResenaServiceService } from 'src/app/Library/Services/resena.service.service';

@Component({
  selector: 'app-book-detail',
  templateUrl: './book-detail.component.html',
  styleUrls: ['./book-detail.component.css']
})
export class BookDetailComponent implements OnInit{
  @Input()
  bookLocal:any;

  resenalocal: any;

  constructor(private router: Router,
    private _resenaServiceService: ResenaServiceService
  ){
    this.bookLocal = (JSON.parse(
                        JSON.stringify(this.router.getCurrentNavigation()?.extras.state)
                      )).book;
   }

  ngOnInit(): void {
    this._resenaServiceService.getResenasByBookId(this.bookLocal.idlibro).subscribe(
      data=>{
        this.resenalocal = data;
        console.log(this.resenalocal)
      },error=>{
        console.log(error);
      }
    )
  }

  createResena() {
    this.router.navigateByUrl('createresena', {
      state: {book: this.bookLocal}
    })};
}
<div class="container" style="width: 80%; border-color: blue; border: 10px;">
  <h2 class=" d-flex justify-content-center">Detalles {{bookLocal.titulolibro}}</h2>
  <div class=" d-flex justify-content-center">
    <label>Titulo: </label>
    <label> {{bookLocal.titulolibro}}</label>
  </div>
  <div class=" d-flex justify-content-center">
    <label>Autor: </label>
    <label> {{bookLocal.autorlibro}}</label>
  </div>
  <div class=" d-flex justify-content-center">
    <label>Categoria: </label>
    <label>{{bookLocal.idcategoriaNavigation.nombrecategoria}}</label>
  </div>
  <div class=" d-flex justify-content-center">
    <label>Resumen: </label>
    <label> {{bookLocal.resumenlibro}}</label>
  </div>
</div>
<button class="btn btn-outline-primary d-flex justify-content-center" (click)="createResena()">Agregar Reseña</button>
<div class="container" style="width: 80%;">
  <h2 class=" d-flex justify-content-center">Reseñas</h2>
  <div *ngFor="let resena of resenalocal">
    <div >
      <label>Descripción: {{resena.descripcionresena}}</label><br/>
      <label>Fecha: {{resena.fecharesena | date}}</label><br/>
      <label>Calificación: {{resena.calificacionresena}}</label><br/>
      <label>Usuario: {{resena.aspnetuser}}</label><br/>
      </div><br/>
  </div>
</div>
