import { Component, OnInit } from '@angular/core';
import { AspNetUserData } from '../AspNetUserData.Service';
import { LogoutServiceService } from '../Autenticacion/Services/logout/logout.service.service';
import { Router } from '@angular/router';

@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.css']
})
export class NavComponent implements OnInit {


  aspNetUserNav: any = this.aspNetUserData.aspNetUserValue$;


  ngOnInit(): void {
    this.aspNetUserNav = this.aspNetUserData.aspNetUserValue$;
    this.aspNetUserNav = this.aspNetUserNav.value;
    console.log(this.aspNetUserNav);
  }

  constructor(private aspNetUserData: AspNetUserData,
    private _logoutService: LogoutServiceService,
    private router: Router
  ){

  }

  logout() {
      this._logoutService.GetLogOut().subscribe(
        data=>{
          if(data.status == 200){
            this.aspNetUserNav = '';
            this.router.navigate(['login']);
            console.log('logout');
            console.log(this.aspNetUserNav);
          }
      },
    error=>{
      console.log(error);
    })
    }
}
<div style="background-color: #f7f8f8;">
  <div class="row" *ngIf="aspNetUserNav == '' || aspNetUserNav == undefined ">
      <div class="col"></div>
      <div class="col"></div>
      <div class="col"></div>
      <div class="col d-flex justify-content-end">
        <a class="link-opacity-100" [routerLink]="['login']">Login</a>
      </div>
  </div>
  <div class="row" *ngIf="aspNetUserNav != '' && aspNetUserNav != undefined ">
    <div class="col"></div>
    <div class="col"></div>
    <div class="col">{{aspNetUserNav | async}}</div>
    <div class="col d-flex justify-content-end">
      <a class="link-opacity-100" (click)="logout()">Logout</a>
    </div>
</div>
</div>
