import { Component, Input, OnInit } from '@angular/core';
import { BookServiceService } from 'src/app/Library/Services/book.service.service';
import { CategoryServiceService } from './../../../Services/category.Service.service'
import { Router } from '@angular/router';

@Component({
  selector: 'app-book-detail',
  templateUrl: './book-detail.component.html',
  styleUrls: ['./book-detail.component.css']
})
export class BookDetailComponent implements OnInit {


  @Input()
  bookLocal:any;

  nombrecategory:string = "";
  constructor(
    private _categoryService: CategoryServiceService,
    private router: Router){  }

  ngOnInit(): void {
    this._categoryService.getCategoryById(this.bookLocal.idcategoria).subscribe(
      data=>{
        if(data.status == 200){
          this.nombrecategory = data.body.nombrecategoria;
        }
      }
    )
  }

  showDetails() {
      console.log("mostrando detalles")
    }
}
<div class="container" style="width: 80%;" (click)="showDetails()">
  <h2 class=" d-flex justify-content-center">Detalles {{bookLocal.titulolibro}}</h2>
  <div class=" d-flex justify-content-center">
    <label>Titulo: </label>
    <label> {{bookLocal.titulolibro}}</label>
  </div>
  <div class=" d-flex justify-content-center">
    <label>Autor: </label>
    <label> {{bookLocal.autorlibro}}</label>
  </div>
  <div class=" d-flex justify-content-center">
    <label>Categoria: </label>
    <div *ngIf="nombrecategory == ''">
      <label>{{bookLocal.idcategoria}}</label>
    </div>
    <div *ngIf="nombrecategory != ''">
      <label>{{nombrecategory}}</label>
    </div>
  </div>
  <div class=" d-flex justify-content-center">
    <label>Resumen: </label>
    <label> {{bookLocal.resumenlibro}}</label>
  </div>
</div>
