import { Component, OnInit } from '@angular/core';
import { BookServiceService } from 'src/app/Library/Services/book.service.service';
import { CategoryServiceService } from 'src/app/Library/Services/category.Service.service';

@Component({
  selector: 'app-list-books',
  templateUrl: './list-books.component.html',
  styleUrls: ['./list-books.component.css']
})
export class ListBooksComponent implements OnInit {

  booksList:any[] = [];
  categoryList:any[] = [];

  constructor(private _bookService: BookServiceService,
    private _categoryService: CategoryServiceService){

  }

  ngOnInit(): void {
    const listBooks = this._bookService.getAllBooks().subscribe(
      data=>{
        this.booksList = data;
        console.log(this.booksList);
      },error=>{
        console.error(error);
      });
    const uniqueCategory = [... new Set(this.booksList.map(x => x.idcategoria))];
    console.log("uniqueCategory: " + uniqueCategory)
    uniqueCategory.forEach((x)=>{
      this._categoryService.getCategoryById(x).subscribe(
        data=>{
          this.categoryList.push(data);
        },error=>{
          console.error(error);
        }
      );
    });
    console.log(this.categoryList)
  }
}
